<script>
    document.addEventListener('DOMContentLoaded', function() {
        const header = document.getElementById('header');
        let headerHeight = header.offsetHeight;

        // Crear e insertar el placeholder 
        const placeholder = document.createElement('div');
        placeholder.id = 'header-placeholder';
        placeholder.style.height = `${headerHeight}px`;
        header.parentNode.insertBefore(placeholder, header);

        function handleScroll() {
            if (window.scrollY > 0) {
                header.classList.add('fixed', 'top-0', 'w-full', 'shadow-lg', 'bg-black');
                placeholder.style.display = 'block'; // muestra el espacio reservado
                placeholder.style.height = `${header.offsetHeight}px`;
            } else {
                header.classList.remove('fixed', 'top-0', 'w-full', 'shadow-lg');
                placeholder.style.display = 'none'; // oculta el espacio cuando no es fijo
            }
        }

        // Scroll optimizado 
        let ticking = false;
        window.addEventListener('scroll', function() {
            if (!ticking) {
                window.requestAnimationFrame(function() {
                    handleScroll();
                    ticking = false;
                });
                ticking = true;
            }
        });

        
        handleScroll();
        window.addEventListener('resize', function() {
            headerHeight = header.offsetHeight;
            placeholder.style.height = `${headerHeight}px`;
        });
    });
</script>
<script>
    // Toggle advanced filters
    document.querySelector('.category-btn').addEventListener('click', function() {
        const advancedFilters = document.getElementById('advancedFilters');
        advancedFilters.style.display = advancedFilters.style.display === 'none' ? 'block' : 'none';
    });
    const header = document.getElementById('header');
    const headerOffset = header.offsetTop;

    window.addEventListener('scroll', function() {
        if (window.scrollY > headerOffset) {
            header.classList.add('sticky');
        } else {
            header.classList.remove('sticky');
        }
    });
    


</script>
